        public static void Game_Log_Ack(Client c, int count)
        {
            using (OutPacket plew = new OutPacket(ServerMessage.GAMELOG))
            {

                plew.WriteInt(0); // length + CRC
                plew.WriteInt(0);
                plew.WriteInt(count);
                plew.WriteInt(ServerConstants.CLIENT_VERSION);
                plew.WriteInt(15199);
                plew.WriteInt(12615854); // TimeLogin
                plew.WriteBytes(new byte[] { 127, 0, 0, 1 });
                plew.WriteLong(c.SessionID); // Key

                c.Send(plew);
            }
        }

        public static void updateCharacterHpSp(Client c, int hp, short sp, short maxFury, short fury)
        {
            using (OutPacket plew = new OutPacket(ServerMessage.CHAR_HPSP))
            {
                plew.WriteInt(0); // length + CRC
                plew.WriteInt(0);
                plew.WriteInt(hp);
                plew.WriteShort(sp);
                plew.WriteShort(maxFury);
                plew.WriteShort(fury);
                plew.WriteShort(-1);

                c.Send(plew);
            }
        }

        public static void FW_DISCOUNTFACTION(Client c)
        {
            using (OutPacket plew = new OutPacket(ServerMessage.FW_DISCOUNTFACTION))
            {
                plew.WriteInt(0); // length + CRC
                plew.WriteInt(0);
                plew.WriteHexString("00 00 00 00 64 00 00 00 00 00 00 00 64 00 00 00 00 70 40 00 E8 03 D2 A8 74 A9 00 00 84 D1");

                c.Send(plew);
            }
        }

