                plew.WriteShort(0);     // 技能ID
                plew.WriteShort(0);     // 技能ID
                plew.WriteShort(0);     // 技能ID
                plew.WriteShort(0);     // 技能ID
                plew.WriteShort(0);     // 技能ID
                plew.WriteShort(0);     // 技能ID
                plew.WriteShort(0);     // 技能ID
                plew.WriteShort(0);     // 技能ID
                plew.WriteShort(0);     // 技能ID
                plew.WriteShort(0);     // 技能ID
                                        // Point
                for (int i = 0; i < 2; i++)
                {
                    plew.WriteByte(20);         // 技能(10301) Point
                    plew.WriteByte(20);         // 技能(10302) Point
                    plew.WriteByte(20);         // 技能(10304) Point
                    plew.WriteByte(20);         // 技能(10303) Point
