        public static void getInvenEquip1(Client c, List<Item> item)
        {
            using (OutPacket plew = new OutPacket(ServerOpcode.INVEN_EQUIP1))
            {
                plew.WriteInt(0); // length + CRC
                plew.WriteInt(0);
                for (int i = 0; i < 24; i++)
                { // 物品編號
                    int itemId = 0;
                    foreach (Item im in item)
                    {
                        itemId = (im.type == 1 && im.slot == i ? im.ItemID : 0);
                        if (itemId != 0)
                            break;
                    }
                    plew.WriteInt(itemId);
                }
                for (int i = 0; i < 24; i++)
                { // 
                    plew.WriteByte(0);
                }
                for (int i = 0; i < 24; i++)
                { // 
                    plew.WriteHexString("00 00 00 00 00 00 00 00 00 00");
                }
                for (int i = 0; i < 24; i++)
                { // 封印物
                    plew.WriteShort(0);
                }
                for (int i = 0; i < 24; i++)
                { // 物品Lock
                    plew.WriteByte(0);
                }
                for (int i = 0; i < 24; i++)
                { // 截止日期
                    plew.WriteInt(-1);
                }
                for (int i = 0; i < 24; i++)
                { // 物品標誌
                    plew.WriteShort(0);
                }
                for (int i = 0; i < 24; i++)
                { // 960 Bytes
                    plew.WriteHexString("00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00");
                }
                for (int i = 0; i < 24; i++)
                { // 192 Bytes
                    plew.WriteInt(0);
                }
                //for (short i = 1; i <= 24; i++)
                //{ // 192 Bytes
                //    plew.WriteInt(0);
                //}
                c.Send(plew);
            }
        }

