        public static void createAllMonster(Client c, Monster monster)
        {
            using (OutPacket plew = new OutPacket(ServerOpcode.MON_ALLCREATE))
            {
                plew.WriteInt(0); // length + CRC
                plew.WriteInt(0);
                plew.WriteInt(50);
                for (int i = 0; i < 50; i++)
                {//??
                    plew.WriteByte(1);
                }
                for (int i = 0; i < 50; i++)
                {//Level
                    plew.WriteByte(monster.Level);
                }
                for (int i = 0; i < 50; i++)
                {//??
                    plew.WriteByte(0);
                }
                for (int i = 0; i < 50; i++)
                {//Facing
                    plew.WriteByte(monster.Facing);
                }
                for (int i = 0; i < 50; i++)
                {//??
                    plew.WriteBytes(1);
                }
                for (int i = 0; i < 50; i++)
                {//??
                    plew.WriteBytes(0);
                }
                for (int i = 0; i < 50; i++)
                {//IN MAP ID
                    plew.WriteShort(i);
                }
                for (int i = 0; i < 50; i++)
                {//Mob X
                    plew.WriteShort(monster.PositionX);
                }
                for (int i = 0; i < 50; i++)
                {//Y
                    plew.WriteShort(monster.PositionY);
                }
                for (int i = 0; i < 50; i++)
                {//??
                    plew.WriteInt(0);
                }
                for (int i = 0; i < 50; i++)
                {//??
                    plew.WriteInt(0);
                }
                for (int i = 0; i < 50; i++)
                {//??
                    plew.WriteShort(5);
                }
                for (int i = 0; i < 50; i++)
                {//??
                    plew.WriteShort(6);
                }
                for (int i = 0; i < 50; i++)
                {//??
                    plew.WriteShort(5);
                }
                for (int i = 0; i < 50; i++)
                {//??
                    plew.WriteShort(1);
